var textMemory = [];
function saveMemory() { textMemory.push(document.getElementById("chat_message").value); document.getElementById("speakText3").innerHTML = textMemory.length };
function sayMemory(a) { socket.emit("talk", { text: textMemory.at(a) }) };
function sayMemoryAlt(a) { sayMemory(document.getElementById("speakText2").value) };
var keySounds = ["./sfx/key1.mp3", "./sfx/key3.mp3"];
function wiiGo() { var kpo = new Audio("./sfx/go.mp3"); kpo.play() }
function wiiClick() { var kpo = new Audio("./sfx/select.mp3"); kpo.play() }
function typeKeyb() { var kpo = new Audio(keySounds[Math.floor(Math.random() * keySounds.length)]); kpo.play() }